(601, 185, '_curriculo_lattes', 'field_6000f9f083e8c'),
(602, 185, 'tl_sobre', 'Pernambucana da capital, filha de Gilda e José, irmã de três mulheres, mãe do coração de dois meninos e tia de lindas garotinhas.'),
(603, 185, '_tl_sobre', 'field_6000fa2083e8d'),
(604, 185, 'tl_empoderamento', 'Integrante do Coletivo Filhas do Vento e Acadêmicas Negras.'),
(605, 185, '_tl_empoderamento', 'field_6000fa3383e8e'),
(606, 185, 'tl_formacao', 'Sou múltipla, já atuei como Auxiliar de Enfermagem, Professora da Educação Infantil, Assistente Social, especialista em Saúde Pública, além disso tudo, sou mestra e doutora em Serviço Social atuando como Docente no Departamento de Serviço Social da UFPE.'),
(607, 185, '_tl_formacao', 'field_6000fb6e62c3d'),
(608, 185, 'tl_bonus', 'Dedico meus conhecimentos e vida na soma de esforços pelo fim das opressões e desigualdades.'),
(609, 185, '_tl_bonus', 'field_6000fedae20ae'),
(610, 185, 'esquema_de_cor', '#417505'),
